sys_script_include.script:
var CTXClientRequest = Class.create();
CTXClientRequest.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getApplications:function(){

		var appProvision = new CTXAppProvision();
		return JSON.stringify(appProvision.getApplications());

	},

	_getUserObjects:function(sys_ids){
		var users = new GlideRecord('sys_user');
		users.addQuery('sys_id','IN',sys_ids);
		users.query();

		var userArr = [];
		var userObj;
		while(users.next()){
			userObj = {
				sys_id: users.getValue('sys_id'),
				name: users.getValue('name')
			};
			userArr.push(userObj);
		}
		return userArr;
	},

	_getVariables:function(ritm_sys_id){
		var logger = new Logger();

		var variable = new GlideRecord('sc_item_option_mtom');
		variable.addQuery('request_item',ritm_sys_id);
		variable.query();

		var variables = {};
		var varObj;
		var name;
		var value;
		var sys_id;
		while(variable.next()){
			name = variable.sc_item_option.item_option_new.name.toString();
			value = variable.sc_item_option.value.toString();
			sys_id = variable.getValue('sc_item_option');

			if(name == 'users'){
				value = this._getUserObjects(value);
			}
			varObj = {
				value:value,
				sys_id:sys_id
			};

			variables[name] = varObj;			

		}

		return variables;
	},

	getRITMAndVariables:function(){
		var ritm_sys_id = this.getParameter('sysparm_ritm_sys_id');
		return this._getRITMAndVariables(ritm_sys_id);
	},

	_getRITMAndVariables:function(ritm_sys_id){
		var logger = new Logger();

		var sc_req_item = new GlideRecord('sc_req_item');
		sc_req_item.get(ritm_sys_id);
		var ritm = {};

		if(!gs.nil(sc_req_item)){
			ritm.sys_id = sc_req_item.getValue('sys_id');
			ritm.number = sc_req_item.getValue('number');
			ritm.due_date = sc_req_item.getValue('due_date');
			ritm.state = sc_req_item.getElement('state').getDisplayValue();
			ritm.variables = this._getVariables(ritm.sys_id);
			return JSON.stringify(ritm);
		}
		logger.logError("NO REQUEST",ctx_request_sys_id);
		return "";
	},

	getCitrixRequest:function(){
		var logger = new Logger();

		var ctx_request_sys_id = this.getParameter('sysparm_ctx_request_sys_id');
		var ctx_request = new GlideRecord('x_wcs_ctrx_auto_sv_citrix_requests');
		ctx_request.get(ctx_request_sys_id);

		if(!gs.nil(ctx_request)){
			return this._getRITMAndVariables(ctx_request.getValue('requested_item'));
		}

		logger.logError("NO REQUEST",ctx_request_sys_id);
		return null;
	},

	_sortDeliveryAndADGroup:function(groupsUpdate){
		var logger = new Logger();
		var sortedGroups = {
			deliveryGroups:[],
			adGroups:[]
		};

		for(var i = 0;i < groupsUpdate.length; i++){
			if(groupsUpdate[i].group_type == 'Delivery Group'){
				sortedGroups.deliveryGroups.push(groupsUpdate[i]);
			}else if(groupsUpdate[i].group_type == 'AD Group'){
				sortedGroups.adGroups.push(groupsUpdate[i]);
			}
		}
		logger.log('sorted',JSON.stringify(sortedGroups));
		return sortedGroups;
	},

	_updateVariable:function(record,variableName,value){

		var varRecordSysId = record.variables[variableName].sys_id;

		var variableItem = new GlideRecord('sc_item_option');
		variableItem.get(varRecordSysId);

		if(!gs.nil(variableItem)){
			variableItem.setValue('value',value);
			variableItem.update();
		}

	},
	updateRITMVariables:function(){
		var logger = new Logger();

		var groupsUpdateString = this.getParameter('sysparm_group_updates');
		var recordString = this.getParameter('sysparm_record');

		try{
			var groupsUpdate = JSON.parse(groupsUpdateString);
			var sortedGroups = this._sortDeliveryAndADGroup(groupsUpdate);

			var deliveryGroups = sortedGroups.deliveryGroups;
			var adGroups = sortedGroups.adGroups;

			var record = JSON.parse(recordString);

			logger.log("delivery groups", deliveryGroups);
			logger.log("ad groups", adGroups);

			if(deliveryGroups.length > 0){
				this._updateVariable(record,'delivery_group_updates',JSON.stringify(deliveryGroups));
			}
			if(adGroups.length > 0){
				this._updateVariable(record,'ad_group_updates',JSON.stringify(adGroups));
			}


			return 'success';
		}catch(error){
			logger.log('Unable to update RITM variables',error);
			return 'failure';
		}


	},

    type: 'CTXClientRequest'
});
