answer = ifScript();

function ifScript() {
	var currGroup;
	var grp;
	var app;
	var users="";
	if(workflow.scratchpad.deliveryGroupUpdates.length > 0){
		grp = workflow.scratchpad.deliveryGroupUpdates.pop();
		for(var appName in grp.applications){
			app = appName;
			break;
		}
		for(var i = 0; i < grp.users.length; i++){
			users += (i < grp.users.length-1)?grp.users[i].sys_id+',':grp.users[i].sys_id;
		}
		currGroup = {
			name: grp.group,
			application: app,
			site_id:grp.site_id,
			users:users
		};
		workflow.scratchpad.currentDeliveryGroup = currGroup;
		return 'yes';
	}
	return 'no';
	
	
}

