answer = ifScript();


function ifScript() {
	
	var checkForMoreUsers = function(){
		if(workflow.scratchpad.userToADGroupPairs.length > 0){
			var groupUserPair = workflow.scratchpad.userToADGroupPairs.pop();
			workflow.scratchpad.current_user = groupUserPair.user;
			workflow.scratchpad.current_group_name = groupUserPair.group;
			if(groupUserPair.group == "group1"){
				workflow.scratchpad.current_group_name = "SNOW_SEC_GRP_TEST";
			}
			return true;
		}
		return false;
	};

	
	if (checkForMoreUsers()) {
		return 'yes';
	}
	return 'no';
}

