(function(){
	var logger = new Logger();
	
	var deliveryGroupUpdatesString = current.variables.delivery_group_updates;
	
	workflow.scratchpad.deliveryGroupUpdates = '';
	workflow.scratchpad.currentDeliveryGroup = '';
	workflow.scratchpad.parseError = null;
	try{
		if(!gs.nil(deliveryGroupUpdatesString)){
			workflow.scratchpad.deliveryGroupUpdates = JSON.parse(deliveryGroupUpdatesString);
		}		
		
	}catch(error){
		logger.logError('Error in Assign Application workflow',error);
		workflow.scratchpad.parseError = error;
	}
	
})();

