(function(){
	var logger = new Logger();
	
	workflow.scratchpad.current_user = {};
	workflow.scratchpad.current_group_name = '';
	workflow.scratchpad.userToADGroupPairs = [];
	workflow.scratchpad.adGroupParseError = null;
	
	var adGroupWithUsersString = current.variables.ad_group_updates;
	var userToADGroupPairs = [];
	
	var userTool = new CTXUserTools();
	
	try{
		var adGroupWithUsers = JSON.parse(adGroupWithUsersString);
		var groupName;
		var users;
		var groupUserPair;
		for(var i = 0; i < adGroupWithUsers.length; i++){
			groupName = adGroupWithUsers[i].group;
			users = adGroupWithUsers[i].users;
			
			for(var u = 0;u < users.length; u++){
				groupUserPair = {
					user:{
						user_name:userTool.getUserName(users[u].sys_id),
						ldap_server: userTool.getUserLDAPServer(users[u].sys_id)
					},					
					group: groupName
				};
				
				userToADGroupPairs.push(groupUserPair);
			}
			
		}
		workflow.scratchpad.userToADGroupPairs = userToADGroupPairs;
	}catch(error){
		logger.log("Error in Application Provisioning workflow",error);
		workflow.scratchpad.adGroupParseError = error;
	}		
	
})();

