(function(){
	var logger = new Logger();		
	
	var chosenCatalogString = current.variables.chosen_catalog;
	
	workflow.scratchpad.username = ''; 
	workflow.scratchpad.delgrp = '';
	workflow.scratchpad.siteid = '';
	
	workflow.scratchpad.parseError = '';
	try{
		
		var chosenCatalog = JSON.parse(chosenCatalogString);
		if(!gs.nil(chosenCatalog)){
			workflow.scratchpad.username = current.request.requested_for.user_name;
			workflow.scratchpad.delgrp = chosenCatalog.DesktopGroup.Name;
			workflow.scratchpad.siteid = chosenCatalog.SiteId;
		}		
		
	}catch(error){
		logger.logError('Error in Desktop Provisioning workflow',error);
		workflow.scratchpad.parseError = error;
	}
	
})();

